INSERT INTO `unit_comments` VALUES (/*id*/1091004, /*unit_id*/109101, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/5.5, /*change_face*/2, /*description*/'要我摸摸你的头吗？\n还是要揉肩？枕大腿？\n巧克力附赠\n姐姐宠溺的照料怎么样？');
