INSERT INTO `unit_comments` VALUES (/*id*/1115003, /*unit_id*/111501, /*use_type*/1, /*voice_id*/3, /*face_id*/3, /*change_time*/8.4, /*change_face*/1, /*description*/'来，小子，取悦我吧！\n今天是特别的日子，\n要是让我无聊，就等着没命吧！\n不过，我会宽松点评分的♪');
INSERT INTO `unit_comments` VALUES (/*id*/1115004, /*unit_id*/111501, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/6.1, /*change_face*/2, /*description*/'哎呀，你在发抖。\n是觉得冷吗？真是太软弱了。\n到我的外套里来吧？\n呵，不用客气♪');
