INSERT INTO `unit_comments` VALUES (/*id*/1115006, /*unit_id*/111531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'小子，怎么样。\n这绝美的景色跟圣夜很相称吧？\n你就尽管感谢我吧。你可是\n我第一个带来这里的人♪');
