INSERT INTO `unit_comments` VALUES (/*id*/1115010, /*unit_id*/111531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'真是个愉快的圣诞节啊。\n下次也要陪我哦？\n呵呵，不许忘了哦？\n跟我的誓言一定要遵守♪');
INSERT INTO `unit_comments` VALUES (/*id*/1115011, /*unit_id*/111501, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/6.5, /*change_face*/1, /*description*/'哼。今天是你的生日？\n你怎么一脸期待啊？\n啊哈哈，如果想要礼物，\n你也应该有点表示吧！');
INSERT INTO `unit_comments` VALUES (/*id*/1115012, /*unit_id*/111531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'为了庆祝你的生日，\n克莉丝提娜小姐可是\n特地准备了礼物哦♪\n我很期待你的反应☆');
