INSERT INTO `unit_comments` VALUES (/*id*/1116007, /*unit_id*/111631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'要用我的演唱会来\n炒热圣诞节的气氛咯♪\n因为圣诞老人和偶像\n都是为人们带去梦想的工作啊！');
INSERT INTO `unit_comments` VALUES (/*id*/1116008, /*unit_id*/111631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'圣诞快乐！\n我会比\n彩灯更加耀眼的！');
INSERT INTO `unit_comments` VALUES (/*id*/1116009, /*unit_id*/111631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我想象着你，\n改编出了圣诞歌的另一个版本。\n虽然有点难为情，\n但我希望你能听一听！');
INSERT INTO `unit_comments` VALUES (/*id*/1116010, /*unit_id*/111631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'这是我有生以来\n最棒的圣诞节了！\n跟你度过的这最美好的一天\n我是绝对不会忘记的！');
