INSERT INTO `unit_comments` VALUES (/*id*/1116012, /*unit_id*/111631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'生日快乐！\n我唱首生日歌为你庆祝吧！\n这是只为你一人而开的演唱会，\n你就好好享受吧！');
INSERT INTO `unit_comments` VALUES (/*id*/1117001, /*unit_id*/111701, /*use_type*/1, /*voice_id*/1, /*face_id*/4, /*change_time*/3.1, /*change_face*/1, /*description*/'虽然我不太喜欢“神圣的夜晚”之类的，\n不过难得举办晚宴，\n就尽情享受吧♪');
INSERT INTO `unit_comments` VALUES (/*id*/1117002, /*unit_id*/111701, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/4.1, /*change_face*/5, /*description*/'这一身衣服能充分展现出\n妾身的美貌……\n穿着这身衣服\n去诱惑那个人也不错呢♪');
INSERT INTO `unit_comments` VALUES (/*id*/1117003, /*unit_id*/111701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/3.7, /*change_face*/2, /*description*/'妾身现在是\n圣诞老人，\n我就来帮帮忙，\n让圣夜更加热闹吧');
INSERT INTO `unit_comments` VALUES (/*id*/1117004, /*unit_id*/111701, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/5.2, /*change_face*/4, /*description*/'说起圣夜的宴会，\n就必须要吃火鸡和蛋糕呢♪\n你、你那是什么眼神？\n妾、妾身才没有得意忘形呢！');
INSERT INTO `unit_comments` VALUES (/*id*/1117005, /*unit_id*/111701, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.0, /*change_face*/5, /*description*/'身穿这身衣服，\n却不准备礼物，那就太不识风趣了……\n没、没办法，我就给那个人\n准备一份礼物吧！');
INSERT INTO `unit_comments` VALUES (/*id*/1117006, /*unit_id*/111731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'圣、圣诞节是圣诞老人\n赠人礼物的节日，\n现在妾身就是圣诞老人……\n总、总之你给我收下，笨蛋！');
INSERT INTO `unit_comments` VALUES (/*id*/1117007, /*unit_id*/111731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'根据茜里借给我的书，应该说……\n伊莉亚圣诞老人会给你\n圣诞礼物哦☆\n这种话我怎么说得出口啊～');
INSERT INTO `unit_comments` VALUES (/*id*/1117008, /*unit_id*/111731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'妾身乃是统治黑夜之人，\n就让妾身送你礼物吧！\n你就心怀感激地收下吧！\n嗯，这样就完美了！');
