INSERT INTO `unit_comments` VALUES (/*id*/1117010, /*unit_id*/111731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'到、到时间了！\n礼物……准备好了！\n心、心心心理准备也……好了！\n接下来将礼物给他就行了！');
INSERT INTO `unit_comments` VALUES (/*id*/1117011, /*unit_id*/111701, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/4.8, /*change_face*/2, /*description*/'妾身记得，\n今天是汝的生日对吧\n那就由妾身\n来为汝庆祝吧');
INSERT INTO `unit_comments` VALUES (/*id*/1117012, /*unit_id*/111731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'今天是那个人的生日！\n要送礼物给他，并且表示感谢……\n诶……汝何时在那里的……\n忘、忘了刚才的事，你这个笨蛋！');
INSERT INTO `unit_comments` VALUES (/*id*/1118001, /*unit_id*/111801, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/5.9, /*change_face*/1, /*description*/'新年快乐\n今年也要吃好多\n好吃的东西！\n这是新年的第一个约定呢☆');
INSERT INTO `unit_comments` VALUES (/*id*/1118002, /*unit_id*/111801, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/3.6, /*change_face*/2, /*description*/'今年的理想\n你已经决定好了吗？\n我的话……是希望大家能够幸福地\n生活在这个世界上。');
INSERT INTO `unit_comments` VALUES (/*id*/1118003, /*unit_id*/111801, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/3.0, /*change_face*/2, /*description*/'这件和服，我穿着好看吗？\n发型也和平时不一样，\n特别允许你\n靠近一点看哦☆');
