INSERT INTO `unit_comments` VALUES (/*id*/1118005, /*unit_id*/111801, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/5.4, /*change_face*/7, /*description*/'广场上在放风筝哦！\n放风筝……风筝……粉蒸……\n感觉肚子\n饿了呢！');
INSERT INTO `unit_comments` VALUES (/*id*/1118006, /*unit_id*/111831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我用心做了年菜哦♪\n虽然卖相可能不太好，\n但味道我可以保证！\n来，请品尝吧☆');
INSERT INTO `unit_comments` VALUES (/*id*/1118007, /*unit_id*/111831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'年菜好像还有驱魔的含义，\n那用魔物来做年菜……\n也就意味着成功打倒了魔物，\n所以是没问题的对吧☆');
INSERT INTO `unit_comments` VALUES (/*id*/1118008, /*unit_id*/111831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'今年也想像这样\n和大家一起去吃\n好吃的东西呢！');
