INSERT INTO `unit_comments` VALUES (/*id*/1118012, /*unit_id*/111831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'生日快乐\n今年也能够像这样和你一起度过，\n我真的很幸福☆\n明年也让我为你庆祝吧？');
