INSERT INTO `unit_comments` VALUES (/*id*/1119003, /*unit_id*/111901, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/8.0, /*change_face*/5, /*description*/'虽然偶尔回去故乡也不错\n不过跟主人分开，我总觉得心里不安\n下次您跟我一起回故乡吧\n这么说会显得很贪心吗？');
INSERT INTO `unit_comments` VALUES (/*id*/1119004, /*unit_id*/111901, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/4.9, /*change_face*/1, /*description*/'甜酒能\n缓解疲劳。如果您觉得累，\n我随时都可以帮您把甜酒吹凉，\n喂您喝下哦');
INSERT INTO `unit_comments` VALUES (/*id*/1119005, /*unit_id*/111901, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.7, /*change_face*/5, /*description*/'我做了一个特别棒的新年初梦，\n梦里我在侍奉主人\n要是我也能出现在\n主人的新年初梦里就好了……');
