INSERT INTO `unit_comments` VALUES (/*id*/1119011, /*unit_id*/111901, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.9, /*change_face*/5, /*description*/'生日快乐\n如果……如果主人愿意的话\n请允许我和您\n两人单独度过这个日子……');
