INSERT INTO `unit_comments` VALUES (/*id*/1121006, /*unit_id*/112131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'早安！\n你今天是来参拜的吗？\n我正在打扫神殿，\n你看，打扫得很干净呢♪');
INSERT INTO `unit_comments` VALUES (/*id*/1121007, /*unit_id*/112131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'马上就能休息了，\n我能跟你一起去吗？\n我听人介绍了很多\n这个神殿的看点！');
