INSERT INTO `unit_comments` VALUES (/*id*/1121009, /*unit_id*/112131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'恋爱运上写着\n今年“缘分就在身边”呢……\n啊！？　不、不是！\n我这话没什么深意的！');
INSERT INTO `unit_comments` VALUES (/*id*/1121010, /*unit_id*/112131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'你的恋爱运写了什么？\n诶？　姻缘从失败开始？\n难道是跟我……\n啊！　没、没什么！');
