INSERT INTO `unit_comments` VALUES (/*id*/1122001, /*unit_id*/112201, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/6.4, /*change_face*/4, /*description*/'变、变身后连发型都会变！？\n这到底是什么原理……\n唉，还是别想了吧……');
INSERT INTO `unit_comments` VALUES (/*id*/1122002, /*unit_id*/112201, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/5.9, /*change_face*/5, /*description*/'魔法侦探Misty★香澄吗……\n稍微有点开心啊♪\n呃，助手！？\n忘、忘掉我刚才说的话！');
INSERT INTO `unit_comments` VALUES (/*id*/1122003, /*unit_id*/112201, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/5.1, /*change_face*/1, /*description*/'唔唔，好在意周围人的目光……\n嗯……助手，\n你能站在我前面吗？谢谢。\n呵呵。感觉自己像个公主。');
INSERT INTO `unit_comments` VALUES (/*id*/1122004, /*unit_id*/112201, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/3.0, /*change_face*/4, /*description*/'Misty、Theory、\nTruly！\n唉。渐渐开始习惯了，\n这样反而让我心情复杂啊……');
INSERT INTO `unit_comments` VALUES (/*id*/1122005, /*unit_id*/112201, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/6.3, /*change_face*/1, /*description*/'只是走在街上都会被小朋友包围……\n魔法少女还真是受欢迎啊。\n助手，你会不会也喜欢\n魔法少女？');
INSERT INTO `unit_comments` VALUES (/*id*/1122006, /*unit_id*/112231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'Misty、Theory、Truly！\n魔法缎带螺旋！\n魔法侦探Misty★香澄！\n用魔法的力量开始搜查！');
INSERT INTO `unit_comments` VALUES (/*id*/1122007, /*unit_id*/112231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'果然还是有点难为情啊。\n你别一直盯着我看哦？\n喂、喂！太近了太近了！\n真是的，我只能逮捕你这个现行犯了！');
INSERT INTO `unit_comments` VALUES (/*id*/1122008, /*unit_id*/112231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我感觉到一股强大的力量涌出身体。\n不过，穿着这身衣服到处走动，\n那个，裙子轻飘飘的，\n让我有点心神不宁啊……');
INSERT INTO `unit_comments` VALUES (/*id*/1122009, /*unit_id*/112231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'助手，怎么样？\n我看起来像个魔法少女吗？\n这么害臊的样子\n我只能让你一个人看哦。真是的……');
