INSERT INTO `unit_comments` VALUES (/*id*/1122012, /*unit_id*/112231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'用魔法的力量开始搜查！\n为、为了帮你庆祝生日，\n我试着只为你一个人变身了……\n怎、怎么样？');
INSERT INTO `unit_comments` VALUES (/*id*/1123001, /*unit_id*/112301, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/'我变成魔法少女了。\n希望我也能像姐姐一样\n帮助很多的人。\n我是这样想的……诶嘿嘿。');
INSERT INTO `unit_comments` VALUES (/*id*/1123002, /*unit_id*/112301, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/5.4, /*change_face*/2, /*description*/'自从成为魔法少女之后，\n我就几乎不再为\n体力而烦恼了。\n感觉就像是完美的故事一样……');
