INSERT INTO `unit_comments` VALUES (/*id*/1123005, /*unit_id*/112301, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/5.0, /*change_face*/2, /*description*/'我走在兰德索尔的街上，\n孩子们就围过来了。\n为了守护那些孩子们，\n我也要更加努力才行……');
INSERT INTO `unit_comments` VALUES (/*id*/1123006, /*unit_id*/112331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'Pure heart！Lock on！\n魔法箭矢，会心一击！\n魔法猎人Purely★栞，\n对准你的心射出爱之箭！');
INSERT INTO `unit_comments` VALUES (/*id*/1123007, /*unit_id*/112331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'兰德索尔的和平就由我来守护。\n爱与希望的力量充满全身……\n将爱注入这一箭！');
INSERT INTO `unit_comments` VALUES (/*id*/1123008, /*unit_id*/112331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'这里就交给我吧！\n我要打倒反物质兽，\n夺回大家的笑容！');
INSERT INTO `unit_comments` VALUES (/*id*/1123009, /*unit_id*/112331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'成为了魔法少女，\n说不定让我自信一点了。\n我已经做好守护这个城市的准备了，\n也包括你……');
INSERT INTO `unit_comments` VALUES (/*id*/1123010, /*unit_id*/112331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'只要反物质兽还会袭击城市，\n魔法少女就会继续战斗。\n请你陪在我身边哦……\n你的存在能给予我力量！');
