INSERT INTO `unit_comments` VALUES (/*id*/1124001, /*unit_id*/112401, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.8, /*change_face*/2, /*description*/'千里之行，始于足下。\n以出色的偶像为目标，\n我要努力训练！');
