INSERT INTO `unit_comments` VALUES (/*id*/1143003, /*unit_id*/114301, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/4.6, /*change_face*/1, /*description*/'魔法少女是给大家\n带来梦想的存在……\n为了不败坏魔法少女之名，\n我也得好好努力才行呢！');
