INSERT INTO `unit_comments` VALUES (/*id*/1143006, /*unit_id*/114331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'Shiny、Feathery、Glory！\n魔法之剑、锋利无比！\n魔法剑士Shiny★智！\n用闪亮的剑术斩断罪恶！');
INSERT INTO `unit_comments` VALUES (/*id*/1143007, /*unit_id*/114331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'即使变成了魔法少女，\n御久间流剑术也没有弱点。\n要不要用你的身体\n来确认一下？');
