INSERT INTO `unit_comments` VALUES (/*id*/1702006, /*unit_id*/170231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'抽签或者占卜\n出了不好的结果反而让人斗志昂扬！\n我会想，\n就由我来改变这未来！');
