INSERT INTO `unit_comments` VALUES (/*id*/1032009, /*unit_id*/103231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'有你在我身边\n能够让我变得\n有多坚强，\n你意识到了吗？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032010, /*unit_id*/103231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'和你在一起，\n我就能坚定地\n向着任何地方前进！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032011, /*unit_id*/103201, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/'今天是你的生日？\n这样啊，生日快乐！\n马上举行\n庆祝派对吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032012, /*unit_id*/103231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'祝你生日快乐\n在你的生日\n面前，不管是什么样的宝石\n都会黯然失色！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032013, /*unit_id*/103261, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'哦呵呵呵！\n热情的火焰滚滚燃烧！\n现在的我完美无缺！　要好好跟上，\n不要落下了哦！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032014, /*unit_id*/103261, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'果然红色很适合我呢。\n将这一切燃烧殆尽的火焰\n对我来说也只是\n一个华丽的装饰品而已！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032015, /*unit_id*/103261, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'有您在，\n我就能够像不死鸟一样复生！\n好了，一口气\n全部解决吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032016, /*unit_id*/103261, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'和您在一起，\n就能够了解到\n自己未曾知晓的世界。好了，接下来\n要让我看些什么？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1032017, /*unit_id*/103261, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我愿意托付后背的人\n只有您而已。并且，\n我希望与我并肩共同走下去的人\n也是您。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033001, /*unit_id*/103301, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.0, /*change_face*/2, /*description*/'搞笑之道很深奥的！\n和我一起，\n将搞笑之道贯彻到极致吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033002, /*unit_id*/103301, /*use_type*/1, /*voice_id*/2, /*face_id*/3, /*change_time*/4.0, /*change_face*/4, /*description*/'我最怕的，不是输给敌人\n而是包袱不响！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033003, /*unit_id*/103301, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/3.0, /*change_face*/1, /*description*/'小哥，这牛奶好喝“马”？\n但是产奶的不是马，\n而是牛哦！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033004, /*unit_id*/103301, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.0, /*change_face*/2, /*description*/'小哥，和我一起，\n朝着搞笑界的顶点进发吧！\n觉醒吧，搞笑冠军！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033005, /*unit_id*/103301, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/'小哥，和我一起想包袱吧！\n在“饱腹”了两小时之后……\n开玩笑的！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033006, /*unit_id*/103331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'喂~走一走瞧一瞧！\n伊丽莎白农场的\n牛奶、酸奶、奶酪！\n现场直销咯！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033007, /*unit_id*/103331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'父老乡亲们，看一看尝一尝！\n刚出产的，真的很美味哦~\n我虽然爱开玩笑，\n但不会说谎哦！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033008, /*unit_id*/103331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'城里的大家都这么\n支持我了！\n我绝对要让大家爆笑！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033009, /*unit_id*/103331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我的段子搞笑吗？\n不管怎么说，小哥都是\n我的第一个粉丝\n我们的脑回路一定是相通的！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1033010, /*unit_id*/103331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我、我说……我可以\n叫小哥搭档吗？\n如果不行的话就换成夫婿……\n啊，反了！真羞人！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
