INSERT INTO `unit_comments` VALUES (/*id*/1045003, /*unit_id*/104501, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/3.3, /*change_face*/6, /*description*/'前段时间看的书非常棒……诶？\n内容吗？这、这个……\n还是别问为好哦？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045004, /*unit_id*/104501, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/4.0, /*change_face*/5, /*description*/'虐待狂先生竟然出现在梦里了……\n无论睡着还是醒着，空花\n都一直被控制着呢', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045005, /*unit_id*/104501, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.9, /*change_face*/5, /*description*/'和虐待狂先生独处……\n这、这！？\n空花会受到怎样的对待呢！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045006, /*unit_id*/104531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'虐待狂先生看着污秽的空花的\n冷酷视线\n噫、噫噫！\n啊啊，又让人兴奋起来了！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045007, /*unit_id*/104531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'空花的愿望就是\n丑态毕露之后\n还被虐待狂先生\n整得狼狈不堪！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045008, /*unit_id*/104531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'“被魔物们弄成这样\n你很高兴吧！”\n你是打算这样责问空花吧！？\n不愧是虐待狂先生，真厉害！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045009, /*unit_id*/104531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'空花已经嫁不出去了……\n所以，请让我一生都\n追随虐待狂先生吧！\n请尽情地管教我吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045010, /*unit_id*/104531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'空花已经变成\n没有虐待狂先生就活不下去了……\n这、这份责任\n你会承担的对吧？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045011, /*unit_id*/104501, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.5, /*change_face*/2, /*description*/'生日快乐\n深深插在蛋糕上的蜡烛，\n在渐渐地融化\n请“呼”地……把它们吹灭', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045012, /*unit_id*/104531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'虐待狂先生的生日啊……\n把缠在空花身上的丝带\n粗鲁地扯开，像迫不及待撕开礼物包装纸的\n孩子一样将空花的衣服也……唔呼呼呼', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045013, /*unit_id*/104561, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'好舒服啊♪\n这股让空花身体激昂的力量……\n全部献给虐待狂先生！\n请你随意使用吧……唔呼呼♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045014, /*unit_id*/104561, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'啊！？　虐待狂先生的眼睛\n在对空花说话！\n“让我看看你的哪里成长了”……\n我知道了……请、请看……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045015, /*unit_id*/104561, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'为了迎接虐待狂先生激烈的攻势，\n空花要不断地变强！\n听到了吗，虐待狂先生♪　呲溜♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045016, /*unit_id*/104561, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'虐待狂先生和空花可以心灵感应……\n即便不说话，也可以明白对方的心意。\n现在……你想欺负空花对吧。\n不是吗！？　怎么会！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1045017, /*unit_id*/104561, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'虐待狂先生……不，和你在一起，\n空花一直都很愉快……\n心里还会暖暖的。\n所以，我们要永远在一起哦……呵呵♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
