INSERT INTO `unit_comments` VALUES (/*id*/1063013, /*unit_id*/106361, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'力量，还有思念……我好像\n有点明白什么更重要了。\n我是不是稍微成长了一点呢？\n诶嘿嘿……多亏了你呢。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1063014, /*unit_id*/106361, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我很庆幸来到兰德索尔，\n我在这里经历了很多事，\n还和很多人进行了交流……\n最重要的是遇见了你！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1063015, /*unit_id*/106361, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'守林人的考验还真辛苦啊……\n不过多亏如此，我能感觉到\n自己在一步一步地前进。\n这是不是说明我长大了呢？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1063016, /*unit_id*/106361, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我不会和罗萨莉亚一样。\n我要以自己的方式去努力。\n以后我也要以自己的方式将这份心意……\n哇啊！？　你、你听到了吗！？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1063017, /*unit_id*/106361, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'从第一次见面开始\n你就一直非常温柔呢。\n你这种地方……\n我觉得非、非常……帅气！　嗯！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
