INSERT INTO `unit_comments` VALUES (/*id*/1065001, /*unit_id*/106501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.7, /*change_face*/2, /*description*/'和你一起，\n就会遇到一些有趣的事情吧？\n所以我很期待你哦。\n来，一起去哪玩玩吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
