INSERT INTO `unit_comments` VALUES (/*id*/1065004, /*unit_id*/106501, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/7.6, /*change_face*/2, /*description*/'不要那么担心啦。\n我还没有弱到\n会被小喽啰干掉的地步。\n不信的话，要来试试看吗？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1065005, /*unit_id*/106501, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/5.8, /*change_face*/1, /*description*/'居然能和龙族以外的家伙\n变得这么要好。\n真是从来没想过啊。\n你到底是什么人？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
