INSERT INTO `unit_comments` VALUES (/*id*/1068002, /*unit_id*/106801, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/8.5, /*change_face*/6, /*description*/'哎呀，今天没有助人为乐吗？\n如果想要一个人悠闲度过的话，\n我给你制作一个迷宫吧。\n咦？　你为什么要逃跑？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1068003, /*unit_id*/106801, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/8.5, /*change_face*/6, /*description*/'其实我有些困扰……\n我预约的新游戏\n在这个世界没办法玩！\n这可是很严重的问题啊！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1068004, /*unit_id*/106801, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/7.7, /*change_face*/2, /*description*/'和少年在一起，\n感觉世界还有自己\n都能够朝着不错的方向前进。\n你要一直都保持这样哦。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
