INSERT INTO `unit_comments` VALUES (/*id*/1068006, /*unit_id*/106831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'只要迷宫女王和公主骑士\n联手，无论面对什么样的对手\n都能够赢。\n好了，少年，一起迎击吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
