INSERT INTO `unit_comments` VALUES (/*id*/1094002, /*unit_id*/109401, /*use_type*/1, /*voice_id*/2, /*face_id*/5, /*change_time*/3.6, /*change_face*/2, /*description*/'虽然我没有\n像其他人那样嬉闹……\n但其实我也觉得很享受。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1094003, /*unit_id*/109401, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.3, /*change_face*/2, /*description*/'安总是陪伴在\n独自一人的我身边……\n是她照亮了我的世界。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1094004, /*unit_id*/109401, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/3.7, /*change_face*/2, /*description*/'没想到会来到一个陌生的世界……\n但是，能与像你们这样\n温柔的人邂逅，真是太好了。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1094005, /*unit_id*/109401, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/'我真的非常感谢你……\n要是有什么我帮得上忙的地方\n请尽管说，我会尽力的。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1094006, /*unit_id*/109431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'如果是为了保护大家，\n我就能使出无穷的力量！\n见识见识龙的火焰吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1094007, /*unit_id*/109431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我绝对……\n不会让大家受伤。\n我的火焰，就是为此而燃烧的！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1094008, /*unit_id*/109431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我所欠缺的，\n是不会输给自己的那份坚强……\n而现在，已经没问题了！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1094009, /*unit_id*/109431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'虽然我曾认为\n自己不需要这种力量……\n但现在，我有些为它感到骄傲了。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1094010, /*unit_id*/109431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我还是挺结实的。\n所以……就交给我吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1094011, /*unit_id*/109401, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.3, /*change_face*/2, /*description*/'今天是你的生日啊。\n那个……生日快乐。\n你有什么想要的礼物吗？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1094012, /*unit_id*/109431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'生日快乐。\n这明明是为你庆生的日子，\n我却也开心起来了，真是不可思议。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095001, /*unit_id*/109501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/2.6, /*change_face*/2, /*description*/'这就是传说中的和服啊……\n是既能看，\n又能脱，\n还可以让人旋转起来的魔性服装！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095002, /*unit_id*/109501, /*use_type*/1, /*voice_id*/2, /*face_id*/5, /*change_time*/2.9, /*change_face*/6, /*description*/'哎～呀～～～\n施虐狂先生，你都看到了吗！？\n看着空花旋转的样子\n笑得那么开心……果然是有人格缺陷！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095003, /*unit_id*/109501, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.8, /*change_face*/5, /*description*/'据说穿和服时，\n是不穿那个的……\n施、施虐狂先生，你可不能说\n要亲眼确认一下哦……嘿嘿嘿。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095004, /*unit_id*/109501, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/7.4, /*change_face*/5, /*description*/'啊，腰带松了……\n施虐狂先生，能帮忙重新系紧它吗？\n呲溜。好……\n好棒！　再紧一点！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095005, /*unit_id*/109501, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/5.6, /*change_face*/5, /*description*/'施虐狂先生……\n不，现在应该叫您施虐狂大人……\n空花是服侍施虐狂大人的\n顺从的……呲溜。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095006, /*unit_id*/109531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'这件和服露得有点多，\n感觉有些难为情……不过，\n一想到施虐狂先生正看着自己，\n好像就感觉……呲溜。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095007, /*unit_id*/109531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'哎～呀～～～！\n好像要对这种旋转上瘾了。\n还想、还想继续转～\n嘿嘿、嘿嘿嘿！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095008, /*unit_id*/109531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'穿着这件衣服跳舞，\n感觉能跳得比平时更美……\n而像蝴蝶般起舞的空花\n就会被像蜘蛛一样的施虐狂先生……嘿嘿嘿。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095009, /*unit_id*/109531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'“扒下空花身上的和服后，\n施虐狂先生就像野兽般……”\n嘿嘿嘿……啊！\n忽然感觉施虐狂先生正注视着我！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095010, /*unit_id*/109531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'很在意这条腰带吗？\n这、这就说明你想拉开它，让空花旋转……\n然后把脱去了和服的空花\n就那样美味地……嘿嘿嘿。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095011, /*unit_id*/109501, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.7, /*change_face*/5, /*description*/'今天是个很重要的日子啊……\n是施虐狂先生的生日……呲溜。\n换句话说，就是要用空花的身体\n来庆祝了吧！？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1095012, /*unit_id*/109531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'生日快乐。\n话说回来，施虐狂先生，和服的腰带\n也很像包装礼物的缎带吧\n我就是礼物，开玩笑的……嘿嘿嘿。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096001, /*unit_id*/109601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.9, /*change_face*/2, /*description*/'如果想偷偷溜出去的话，\n我知道一个好地方。\n下次让我来带路吧！\n一定会很有趣的！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096002, /*unit_id*/109601, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.9, /*change_face*/6, /*description*/'我喜欢红叶，\n但也喜欢红叶馒头哦。\n一想起来就感觉饿了……\n正可谓“舍华求实”！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096003, /*unit_id*/109601, /*use_type*/1, /*voice_id*/3, /*face_id*/3, /*change_time*/8.6, /*change_face*/2, /*description*/'扔一次是为了一统天下……\n扔两次也是为了一统天下……\n将军也要来进行\n扔手里剑的特训吗？可以扔个痛快！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096004, /*unit_id*/109601, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/11.0, /*change_face*/2, /*description*/'这副狐狸面具是在\n大江户镇上办庙会时买的。\n走路时铃铛会响，非常可爱。\n嗷嗷！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096005, /*unit_id*/109601, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/4.0, /*change_face*/1, /*description*/'我发明了忍术·冻结地狱。\n强迫敌人吃下\n冰凉的刨冰，\n是能让对方脑袋发痛的招数！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096006, /*unit_id*/109631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'向与将军为敌之人降下天诛！\n在一统天下之前我可不会停下！\n以我的必杀忍术，\n定能百战百胜！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096007, /*unit_id*/109631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'哼哼哼……\n就为将军展露我秘藏的新忍术吧！\n唔，还不到使用它的时候吗？\n将军你真是坏心眼～', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096008, /*unit_id*/109631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'将军定会建立一个\n天下太平的盛世吧！\n而我会一直为将军\n守护那片河山的！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096009, /*unit_id*/109631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'请欣赏我活跃的身姿吧！\n我会时刻不离将军身边，\n总有一天，一定会\n作为将军的忠臣，被人们称颂。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096010, /*unit_id*/109631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'只要是为了将军，无论面对\n怎样的敌人，我都会去战斗！\n因为我相信，忠诚心\n才是一统天下的王牌！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096011, /*unit_id*/109601, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.4, /*change_face*/1, /*description*/'生日快乐！\n我准备的礼物是\n将军专用的手里剑！\n请尽情地扔吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1096012, /*unit_id*/109631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'今天真是恭喜了！\n就让我来领头干杯吧！\n把双手举起来！\n要欢闹到早晨哦！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097001, /*unit_id*/109701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.9, /*change_face*/2, /*description*/'雷姆擅长做所有的家务，\n要是有什么\n雷姆能帮上忙的事，\n请尽管吩咐。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097002, /*unit_id*/109701, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/5.8, /*change_face*/2, /*description*/'姐姐抛下女仆的工作，\n正在休息？\n不愧是姐姐！\n休息的姿态也很优雅！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097003, /*unit_id*/109701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/'虽说是鬼族，\n其实也与人类没什么不同哦。\n只是能横扫魔兽、挥动铁球罢了。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097004, /*unit_id*/109701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'请把照顾起居的工作\n交给雷姆吧。\n雷姆一定会\n全心全意地服侍您。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097005, /*unit_id*/109701, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/5.1, /*change_face*/2, /*description*/'看来在这边的世界，\n哪怕头上长角\n也不值得奇怪呢。\n总觉得有些开心。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097006, /*unit_id*/109731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'这个铁球？\n它是便携式的，\n因为缩短过长度，\n所以比普通的铁球更轻一些哦。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097007, /*unit_id*/109731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'即便是巨木，\n也无法阻挡\n身为鬼族的雷姆。\n就让雷姆来将其全部粉碎。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097008, /*unit_id*/109731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'这种巨木，\n靠雷姆的力量也能移开。\n那、那个，如果成功了，\n也可以夸奖一下雷姆哦？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097009, /*unit_id*/109731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'女孩子无法\n破坏这棵倒下的树木？\n不必多言，\n请站在那边看清楚了。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097010, /*unit_id*/109731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'雷姆很强……\n这样称赞女孩子\n可能不太合适吧？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097011, /*unit_id*/109701, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.4, /*change_face*/1, /*description*/'祝您生日快乐。\n雷姆来做一个蛋糕庆祝吧。\n这次要做得豪华点，\n首先得找来飞龙的蛋才行。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1097012, /*unit_id*/109731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'祝您生日快乐！\n为了今天，\n雷姆做了许多准备，\n让我们来盛大地庆祝一番吧。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098001, /*unit_id*/109801, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/5.0, /*change_face*/3, /*description*/'这里是异世界。说实话，\n尽管确实很让人难以置信……\n但是总比巴鲁斯的油嘴滑舌\n多了那么几分可信度吧。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098002, /*unit_id*/109801, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/2.4, /*change_face*/3, /*description*/'真是陷入麻烦的局面了呢。\n算了，似乎也没有危险人物，\n既然能轻松点，那也没什么问题。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098003, /*unit_id*/109801, /*use_type*/1, /*voice_id*/3, /*face_id*/3, /*change_time*/1.9, /*change_face*/2, /*description*/'拉姆可是有在工作的哦。\n拉姆在用自己品尝红茶的优雅模样，\n使周围的人感到赏心悦目啊。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098004, /*unit_id*/109801, /*use_type*/1, /*voice_id*/4, /*face_id*/3, /*change_time*/5.4, /*change_face*/4, /*description*/'这是风系的魔法。\n只要拉姆愿意，\n可以把任何东西都切成碎片。\n不过那很累人，所以拉姆不会那么做。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098005, /*unit_id*/109801, /*use_type*/1, /*voice_id*/5, /*face_id*/3, /*change_time*/5.9, /*change_face*/2, /*description*/'吃下这个蒸地瓜吧。\n很好吃吧。\n是刚出炉……不，\n刚蒸好的哦。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098006, /*unit_id*/109831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'在拥有千里眼的拉姆看来，\n你是绝对逃不掉的。\n你要是还惜命，\n就尽管发挥自己的智慧与勇气吧。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098007, /*unit_id*/109831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'给我记住了。\n哪怕失去了角，\n本天才拉姆\n也是常人遥不可及的存在。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098008, /*unit_id*/109831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'你在战斗中真是派不上用场呢。\n拉姆还得费一番功夫掩藏失望。\n你可不要……\n从拉姆身边离开哦。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098009, /*unit_id*/109831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'就让我一口气解决掉吧。\n你问为什么？\n因为拉姆……没什么，\n只是想尽快回去罢了。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098010, /*unit_id*/109831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'风魔法很方便哦。不仅能切碎敌人，\n还能把麻烦的男人\n彻底拧断。\n你问拧哪里？任你想象。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098011, /*unit_id*/109801, /*use_type*/2, /*voice_id*/6, /*face_id*/3, /*change_time*/6.5, /*change_face*/2, /*description*/'能让拉姆给你庆祝生日，\n可要感到光荣哦。\n你问礼物是什么？那当然\n是刚蒸好的地瓜咯！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1098012, /*unit_id*/109831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'你想要什么？\n尽管说吧。\n毕竟，今天可是你的生日哦？\n奖励还是得给你的。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099001, /*unit_id*/109901, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/7.9, /*change_face*/1, /*description*/'真是奇妙啊。\n尽管都有玛那和精灵，\n却有这么多没见过的事物。\n这就是异世界吗……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099002, /*unit_id*/109901, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.3, /*change_face*/2, /*description*/'要去散散步吗？\n我要去和精灵们说说话，\n这是和这些孩子们\n缔结契约的条件之一。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099003, /*unit_id*/109901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.0, /*change_face*/6, /*description*/'我刚才去了趟城镇，\n看到了像是精灵的人。\n难道这个世界里\n也有半精灵吗？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099004, /*unit_id*/109901, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/5.2, /*change_face*/6, /*description*/'小可可萝很聪明，\n而且非常可爱。\n如果她真是我的妹妹\n该有多好呢。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099005, /*unit_id*/109901, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.4, /*change_face*/6, /*description*/'自从来到这个世界后，\n帕克就不怎么出来了。\n莫非是担心会用掉太多魔力吗。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099006, /*unit_id*/109931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'你要不要也来坐坐？\n兰德索尔的花也非常漂亮。\n微精灵们也很开心。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099007, /*unit_id*/109931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'来聊聊天吧？\n关于这个世界，\n以及我的世界……\n我有好多话想说。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099008, /*unit_id*/109931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'这样啊，小可可萝她\n也能和精灵们对话啊。\n呵呵，我果然没法\n把她当成是外人。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099009, /*unit_id*/109931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'哎呀，花瓣落在你头发上了。\n我帮你拿下来吧。\n头靠过来一点。\n就是这样……好，拿掉了哦♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099010, /*unit_id*/109931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我说……下次还能\n一起来这个花田看看吗？\n对精灵术士而言，\n承诺是最为重要的事物。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099011, /*unit_id*/109901, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.0, /*change_face*/2, /*description*/'在这边的世界\n要说“happy birthday”\n对吧？\n生日快乐。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1099012, /*unit_id*/109931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'今天是你的生日啊，生日快乐。\n能像这样为你庆生，\n真的让我非常高兴。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1100001, /*unit_id*/110001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.9, /*change_face*/2, /*description*/'哈啰♪夏天的大海闪闪发亮，\n真的超棒对吧！\n我们尽情享受吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1100002, /*unit_id*/110001, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.9, /*change_face*/6, /*description*/'秀才，你知道吗？\n海水可是咸的哦！\n人家很博学吧♪\nThis is understand？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1100003, /*unit_id*/110001, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/3.4, /*change_face*/1, /*description*/'拍摄结束之后，\n我们一起去海边玩吧！\n马上就结束了，\n你等一下哦♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1100004, /*unit_id*/110001, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/2.5, /*change_face*/5, /*description*/'这身泳装很棒吧！\n希望这样\n能让粉丝们高兴♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1100005, /*unit_id*/110001, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/7.7, /*change_face*/1, /*description*/'嗯！大海真是太棒了！\n能跟秀才尽情玩耍\n来这里真是太好了♪\n现在就先别提暑假作业啦～', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
