INSERT INTO `unit_comments` VALUES (/*id*/1127009, /*unit_id*/112731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'虽说我已经习惯了战斗，\n但果然有你在我身边，\n我就会感到放心，能够努力下去……\n谢谢你。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1127010, /*unit_id*/112731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'虽然因为发生了各种事情，\n我的日常发生了改变，\n但你还是一如既往没有变化，\n这让我很开心。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1127011, /*unit_id*/112701, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/6.6, /*change_face*/7, /*description*/'诶，今天是你生日吗？\n太过匆忙没有给你准备什么东西……\n总、总之现在把\n这颗漂亮的橡子送给你！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1127012, /*unit_id*/112731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'生日快乐～就用游戏和\n豆沙面包来为你庆祝吧～\n来吧，我们悠闲地\n尽情享受吧～', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1128001, /*unit_id*/112801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.0, /*change_face*/3, /*description*/'现在的我是牧场的守护神！\n绝不让大家碰它一根汗毛！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1128002, /*unit_id*/112801, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/3.3, /*change_face*/1, /*description*/'变强大后，农活也进行得更加顺利。\n虽然我不是很喜欢战斗，\n不过，取决于使用方式，\n强大的力量也可以为人们带来幸福。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1128003, /*unit_id*/112801, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/6.3, /*change_face*/2, /*description*/'这身打扮\n很受孩子们欢迎！　即使不是段子\n带来的笑声，\n但果然看到笑容还是很开心的！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1128004, /*unit_id*/112801, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/5.4, /*change_face*/5, /*description*/'就、就算是穿上铠甲的样子很少见，\n也不要这么直直地盯着我啊。\n好、好害羞……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1128005, /*unit_id*/112801, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/7.4, /*change_face*/6, /*description*/'我们不止是搞笑，\n连战斗也配合得很默契！\n干脆来当我的新郎吧……\n没、没什么！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1128006, /*unit_id*/112831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'根据外表去判断别人，\n可是会遭殃的哦？\n虽然身体很小，\n但心中隐藏的爱是无限大的！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1128007, /*unit_id*/112831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'现在可不是搞笑的时候！\n扰乱牧场和平的家伙，\n就用我的突击\n将他们击飞到月亮上去！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1128008, /*unit_id*/112831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'多亏了平日里的劳动，\n战斗中身体意外得灵活呢。\n好，就这样一口气\n解决吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1128009, /*unit_id*/112831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'其实，我非常害怕\n战斗。但是，\n如果身旁有小哥你在，\n我就能变得非常强大！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1128010, /*unit_id*/112831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'如果变得太强，\n就没人愿意娶我了。\n到、到那时小哥你……\n还、还是当我没说吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1128011, /*unit_id*/112801, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/'生日快乐！\n已经准备好蛋糕和料理了。\n不过，我们先用刚刚挤出的牛奶\n来干杯吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1128012, /*unit_id*/112831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'你总算来了，小哥！\n今天整个牧场包场\n举行派对呢！\n要尽情享受哦！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1129001, /*unit_id*/112901, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/8.7, /*change_face*/6, /*description*/'穿上这件衣服后，就感觉自己\n好像变成了故事中的主人公一样\n马靠衣装人靠鞍呢！\n哎？　我说错了吗？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1129002, /*unit_id*/112901, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/9.5, /*change_face*/4, /*description*/'这件衣服真不错呢～\n越看越觉得做工很好\n我想脱下来确认一下缝纫方法～\n啊，哥哥你不能看哦！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1129003, /*unit_id*/112901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.7, /*change_face*/6, /*description*/'哎呀，好危险好危险\n像平时那样蹦蹦跳跳之后\n裙子就飘起来了～\n没有被别人看到吧？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1129004, /*unit_id*/112901, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.6, /*change_face*/5, /*description*/'和平时不一样的衣服……\n哥哥可以夸奖我吗？\n如果说我可爱的话，\n我会……心跳加速的哦！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1129005, /*unit_id*/112901, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/4.7, /*change_face*/2, /*description*/'不要乱动哦～\n你问我在做什么？　诶嘿嘿，\n我在测量哥哥的尺寸，\n然后制作同款的衣服?', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1129006, /*unit_id*/112931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我就像故事中的女主角一样?\n当然，男主角就是哥哥你啦！\n但我遇到危机的时候，\n你要帅气地拯救我哦！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1129007, /*unit_id*/112931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我已经不再是\n总是躲在哥哥是身后的我了！\n今后我会陪在哥哥身旁……\n和你一起走下去的?', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1129008, /*unit_id*/112931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'哥哥你看！\n这件衣服非常可爱！\n诶？　你说的确很可爱……\n好、好害羞啊……诶嘿嘿', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1129009, /*unit_id*/112931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'不知道是不是因为这件衣服，\n感觉状态绝佳！\n我会好好瞄准的～\n目标是哥哥的心?', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1129010, /*unit_id*/112931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'用这么～可爱的打扮\n接近哥哥的话，哥哥也会\n心跳加速吗……好！\n我要拿出勇气来！哥哥?', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1129011, /*unit_id*/112901, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.9, /*change_face*/2, /*description*/'今天是哥哥的生日！\n生日快乐！\n诶嘿嘿，哥哥又变得\n更加帅气了呢?', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
