INSERT INTO `unit_comments` VALUES (/*id*/1130001, /*unit_id*/113001, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/3.7, /*change_face*/5, /*description*/'这件衣服……\n就像真的兔子一样，\n对我来说太可爱了……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130002, /*unit_id*/113001, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/5.0, /*change_face*/6, /*description*/'我、我是兔子哦，蹦蹦?\n说笑的……诶？前、前辈，\n你什么时候在那里的……\n请把刚才的事情忘掉吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130003, /*unit_id*/113001, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/9.3, /*change_face*/5, /*description*/'兔、兔子是幸运的象征，\n如果我能一直待在\n前辈的身边……那个……\n果、果然还是没什么！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130004, /*unit_id*/113001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.1, /*change_face*/2, /*description*/'感觉身体比平时\n还要轻……这也是多亏了\n这件寄宿了小爱丽丝的力量的\n衣服吧?', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130005, /*unit_id*/113001, /*use_type*/1, /*voice_id*/5, /*face_id*/3, /*change_time*/11.7, /*change_face*/5, /*description*/'小爱丽丝的力量变强了，\n现在的我也许可以邀请前辈了呢！\n前、前辈！　要不要一起吃个饭……\n果、果然还是做不到！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130006, /*unit_id*/113031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'虽、虽然我很害怕与魔物战斗，\n但我也是\n得到了小爱丽丝力量的人之一！\n该出手时会出手的！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130007, /*unit_id*/113031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'身体好轻……就好像成为了\n真正的兔子一样……\n嘿嘿，现在的我\n也许能得到前辈的夸奖?', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130008, /*unit_id*/113031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我不会让你碰\n前辈一根手指的！\n我会守唔！？\n呜呜……又咬到舌头了……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1130009, /*unit_id*/113031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'不能总是\n依赖前辈们！\n我也要好好加油才行！\n加油，步美！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
