INSERT INTO `unit_comments` VALUES (/*id*/1130011, /*unit_id*/113001, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/8.0, /*change_face*/4, /*description*/'前、前辈！！\n呃……那个，生、生日\n快惹！\n呜呜，又咬舌头了……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
