INSERT INTO `unit_comments` VALUES (/*id*/1138011, /*unit_id*/113801, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.0, /*change_face*/6, /*description*/'虽、虽然你的生日对我来说\n无关紧要，但我还是姑且\n祝贺你一下吧。\n只、只是作为天使哦！？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
