INSERT INTO `unit_comments` VALUES (/*id*/1139001, /*unit_id*/113901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.7, /*change_face*/3, /*description*/'骑士先生，今天是万圣节呢……\n我希望你能陪我一下。\n你、你可不要误会了哦！\n只是想请你帮一下我的工作哦！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1139002, /*unit_id*/113901, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/6.7, /*change_face*/1, /*description*/'怎么了，一直盯着我看……\n难道说，骑士先生\n也想要变装吗？　既然如此……\n真拿你没办法，我也给你缝制一套吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1139003, /*unit_id*/113901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/8.4, /*change_face*/2, /*description*/'看招！　嘿！\n你问我在做什么，当然是在练习\n用线去捆绑别人了。\n目标当然是……你明白的吧？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
