INSERT INTO `unit_comments` VALUES (/*id*/1147001, /*unit_id*/114701, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/5.9, /*change_face*/1, /*description*/'哈哈哈哈！\n新年的第一个笑容要像恶徒一样\n嚣张地大笑才行！　你要不要也\n一起来？　很爽的哦！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147002, /*unit_id*/114701, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/5.3, /*change_face*/3, /*description*/'恶徒可没有休息日！\n就算是新年，我也要战斗！\n好了，有怨言的家伙\n一起上吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147003, /*unit_id*/114701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.8, /*change_face*/8, /*description*/'不是有什么一年之计的说法吗。\n所以在新年里，\n我要像恶徒那样做坏事！　不过，\n说来我一年到头都在做坏事啊！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147004, /*unit_id*/114701, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/8.3, /*change_face*/6, /*description*/'有好多小摊啊！　这样的话，\n不管有多少压岁钱，\n都不够用呢！　我说，你的还没有……\n没、没什么！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147005, /*unit_id*/114701, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/3.9, /*change_face*/2, /*description*/'没想到会和你一起\n度过新年……\n不过，感觉还不坏呢！\n去吃一些好吃的吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147006, /*unit_id*/114731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我要用天楼霸断剑\n将一切都砍成两半！\n不管是天空\n还是大海！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147007, /*unit_id*/114731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'装饰后的天楼霸断剑\n意外还不错呢！\n不过，不管是什么样，\n只要能一刀砍断就行了！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147008, /*unit_id*/114731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'真是让人心情舒畅的景色呢！\n那家伙也在某个地方……\n尾狗刀也像我这样，\n正在欣赏日出吗……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147009, /*unit_id*/114731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'今年你的运气也会很不错哦！\n毕竟有我和\n这把天楼霸断剑在！\n不管什么样的未来，都可以开拓出来！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1147010, /*unit_id*/114731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'你不觉得公主骑士的力量\n和元旦的日出很像吗？\n很温暖、柔和……而且，\n会让人不断涌出力量！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
