INSERT INTO `unit_comments` VALUES (/*id*/1169006, /*unit_id*/116931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'作为领班，\n我会好好教导你的！\n当然，要效率拉满！\n你准备好了吗！？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
