INSERT INTO `unit_comments` VALUES (/*id*/1209001, /*unit_id*/120901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/8.7, /*change_face*/2, /*description*/'我盛装打扮起来还不错吧？\n特别是披在外面的这件，\n我自己也非常喜欢。充满了\n领袖人物的气魄♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209002, /*unit_id*/120901, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/12.0, /*change_face*/2, /*description*/'妾身身为长者，\n有权利给所有人压岁钱……\n既然如此，妾身\n可就要大方地散财了！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209003, /*unit_id*/120901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/6.4, /*change_face*/2, /*description*/'给我那些蝙蝠眷属们\n放个新年假期好了……\n汝可要一直待在妾身身边，\n全年无休哦♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209004, /*unit_id*/120901, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/10.5, /*change_face*/2, /*description*/'这副模样是要展示给庶民们看，\n还是让汝慢慢欣赏呢……\n现在妾身更中意后者。新年伊始，\n汝的气运就很不错啊♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1209005, /*unit_id*/120901, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/4.4, /*change_face*/4, /*description*/'孩童姿态的妾身盛装打扮起来\n很可爱？　就算汝再怎么吹捧，\n妾身也不会变回去的。绝对不会！\n不过，被夸奖的感觉还挺不错的……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
