INSERT INTO `unit_comments` VALUES (/*id*/1214007, /*unit_id*/121431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'唱歌、跳舞、演戏……\n任何事情都能做得很完美\n才是成熟的淑女吧？\n很值得挑战一下♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1214008, /*unit_id*/121431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'这封粉丝来信\n是你写的吧？　你好像终于\n明白我的魅力了！\n我会仔细看的♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1214009, /*unit_id*/121431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'欢迎光临♪\n我要散发成年人的\n费洛蒙来吸引客人！\n这样肯定能生意兴隆！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1214010, /*unit_id*/121431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'其实我对舞台表演\n和这份工作都充满了不安……\n不过，有你在就没问题了。\n所以，那个……一直以来都谢谢了！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1214011, /*unit_id*/121401, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/3.9, /*change_face*/2, /*description*/'生日快乐！\n你平时总是关照我，\n今天就特别由我来\n陪伴你吧♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1214012, /*unit_id*/121431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'生日礼物就是我·自·己♪\n真是的！　反应太冷淡啦！\n这可是我在为你庆祝，\n你要再高兴一点啊！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215001, /*unit_id*/121501, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/'这身怪盗服装……\n太帅了，我穿简直有些\n浪费啊，每次穿上它\n我都会心跳个不停……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215002, /*unit_id*/121501, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/6.5, /*change_face*/4, /*description*/'前前前、前辈！　你有没有\n看到我的面具？　\n就戴在脸上？　啊，\n真、真的呢。啊哈哈哈……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215003, /*unit_id*/121501, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/0.0, /*change_face*/4, /*description*/'之前，我试了试变装，想着或许\n能对怪盗活动有所帮助，\n可是都没人注意到我的存在……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215004, /*unit_id*/121501, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.1, /*change_face*/2, /*description*/'前辈，你能看见我吗？\n呵呵，那我就放心了。\n只要有前辈关注我，\n我就能鼓起勇气！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215005, /*unit_id*/121501, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/6.1, /*change_face*/5, /*description*/'不会被人注意到\n也不全是坏事，这样就能\n和前辈悠闲地相处了……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215006, /*unit_id*/121531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'翱翔夜空的怪盗\n在纸登场！\n啊哇哇，在重要的时候\n吃螺丝了！？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215007, /*unit_id*/121531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'戴上这张面具，我就能\n变得稍微大胆一点……\n但、但是……从天空潜入\n似乎过于大胆了！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215008, /*unit_id*/121531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'呜哇哇，太高了，太高了！\n前前前、前辈！　你抓住我……\n不，请让我抓住你！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215009, /*unit_id*/121531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'没事，没事……\n秘密道具我全都带了……\n不过，就算忘了带东西，\n只要有前辈在就没问题！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215010, /*unit_id*/121531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'进行怪盗活动总是会\n遇到可怕的事情……\n不过，我并不讨厌这种\n心跳加速的感觉♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215011, /*unit_id*/121501, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.9, /*change_face*/2, /*description*/'前辈……祝你……\n生日……快乐！\n太、太好了……慢慢说\n就不会咬到舌头惹！　啊……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1215012, /*unit_id*/121531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'今天是前辈的生日……所以\n我给许多人都发去了\n生日会的预告函！　诶嘿嘿……\n希望气氛会很热烈♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216001, /*unit_id*/121601, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/'哼哼哼！\n我就是神出鬼没的大怪盗！\n盯上的东西一定会弄到手！\n不管是金银财宝还是首领的位子！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216002, /*unit_id*/121601, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/7.1, /*change_face*/6, /*description*/'只要是为了以下克上，\n我会不择手段！\n别怕，只要不被发现就好了！\n被发现的话……会更惨的。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216003, /*unit_id*/121601, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/6.7, /*change_face*/3, /*description*/'这条围巾怎么样啊！\n你说很有大怪盗的感觉，\n让人心潮澎湃？\n啊，不可以拽啦！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216004, /*unit_id*/121601, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/'竟然在我面前发呆，\n你真是好大的胆子啊，哥哥！\n就算今天的点心没了，\n也不关我事哦？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216005, /*unit_id*/121601, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/7.5, /*change_face*/6, /*description*/'我、我什么都没做啊？\n我才没有隐藏气息\n偷听哥哥说话，\n是真的，只是未遂而已啦。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216006, /*unit_id*/121631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'噫啊啊啊啊啊！\n接下来明明应该是\n大怪盗的帅气逃亡戏码！\n为什么……会变成这样啊！？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216007, /*unit_id*/121631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'这……这这这种时候\n更要冷静啊！\n镇定下来，观察情况……\n啊——唔，还在继续下坠！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216008, /*unit_id*/121631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'咿！？　对、对了！\n这种时候觉醒就对了！\n唤醒沉睡的龙族之力！\n虽然现在还是半夜，不过觉醒吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216009, /*unit_id*/121631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'不应该摆招牌动作的！\n失去！　平衡了！　在下坠！\n我遇到危机了啊，哥哥！\n这是给你发呆的时候吗！？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216010, /*unit_id*/121631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'一定……一定要接住我哦？\n我可不是在跟你搞笑哦？\n一定要牢牢抓住我！\n我相信你！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216011, /*unit_id*/121601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/2.5, /*change_face*/2, /*description*/'哥哥的生日？\n我记得哦，是今天对吧？\n你瞧，怎么样！\n竟然怀疑我，你要道歉哦！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1216012, /*unit_id*/121631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'生日快乐！\n真是没办法，为你准备一下礼物\n也不是不行哦。\n来，想要什么，尽管说说看！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219001, /*unit_id*/121901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.4, /*change_face*/2, /*description*/'有了海盗装束和新的魔剑，\n接下来需要的就是……\n呵呵，你明白的吧？\n对！　就是新的必杀技！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219002, /*unit_id*/121901, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/9.6, /*change_face*/6, /*description*/'呵呵，我想出了新的\n海盗守则哦。值得纪念的\n第66个守则！\n诶，太多了？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219003, /*unit_id*/121901, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/7.1, /*change_face*/4, /*description*/'还以为只要穿这身衣服，\n就能不断想出\n《冥风战记》续篇的构思……\n然而现实没有那么美好……嗯。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219004, /*unit_id*/121901, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/7.4, /*change_face*/6, /*description*/'为了控制住新觉醒的深渊之力，\n我换了新的眼罩！\n什么？　你也想戴？\n你、你愿意的话就先用我的吧？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219005, /*unit_id*/121901, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/3.7, /*change_face*/2, /*description*/'你问我穿成这样能不能游泳？\n哼，别小看我，港口城镇的\n大家有教我，我总有一天\n能学会游泳的！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219006, /*unit_id*/121931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'站在船头果然令人心潮澎湃啊！\n桅杆上？　啊，那里\n有点太高了……唉呀，\n那个位置就让给你好了！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219007, /*unit_id*/121931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'这艘船上还没有主炮，\n但我的最强究极歼灭魔法\n就如同主炮一样！\n既是船长也是主炮手……太帅气了！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219008, /*unit_id*/121931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'这风来得太是时候了……\n恰到好处地让外套翻飞，\n又不会把帽子吹走，\n嗯，真是绝妙的风啊！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219009, /*unit_id*/121931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'呵，我想起了自己前世\n在大海上冒险的事迹了。\n灵魂和我连结在一起的你，\n总有一天也会想起来的！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219010, /*unit_id*/121931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'船员、舵手……不，\n还是叫你“搭档”最合适。\n大海盗的独一无二的搭档……\n那就是你，吉格鲁特！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219011, /*unit_id*/121901, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.2, /*change_face*/5, /*description*/'吉格鲁特，生日快乐！\n我为了这一天已经准备好了财宝！\n虽、虽然是我自己亲手做的……\n不介意的话，希望你收下！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1219012, /*unit_id*/121931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'今天是你的生日庆典，盟友啊！\n呵呵，盛宴的准备\n已经完成……就用海盗风格的宴会\n来为你热烈庆祝吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220001, /*unit_id*/122001, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/'乘船旅行好像也很有意思呢。\n下次，我们也去试试好吗？\n比如探索受诅咒的魔法道具，\n或是巡游兰德索尔的灵场……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220002, /*unit_id*/122001, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/10.5, /*change_face*/6, /*description*/'今天风平浪静，天气很好，\n天上的海鸟们也都惬意地\n飞着……诶，你看不到？\n就是说……那是灵鸟吗？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220003, /*unit_id*/122001, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/10.7, /*change_face*/5, /*description*/'这身服装……到底是海盗还是海军？\n只要适合就没问题？\n不是这样啦，那个，唔……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220004, /*unit_id*/122001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/11.2, /*change_face*/2, /*description*/'即使变成幽灵也想实现的梦想……\n要是有的话就好了。\n我现在还不明白这些……\n不过，我想和你一起寻找。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220005, /*unit_id*/122001, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.5, /*change_face*/6, /*description*/'海风吹着好舒服啊，\n不过风有点大，\n会把裙子……哇啊！\n你、你看到了吗？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220006, /*unit_id*/122031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'今晚满天都是星星呢……\n要是时间能在此刻停止该多好……\n我不禁产生了这样的想法。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220007, /*unit_id*/122031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'就算用剑无法拯救\n那些彷徨的灵魂，\n但只要你我合力，\n也是可以拯救的。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220008, /*unit_id*/122031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'你迷茫的时候，\n也让我帮你吧。\n我会像暗夜的灯火一般，\n为你占卜出前进的道路。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220009, /*unit_id*/122031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'只要和你在一起，\n感觉可以去往任何地方，\n即便是这片大海的尽头……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220010, /*unit_id*/122031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'月光和星光……\n还有一望无际的大海……\n世界上仿佛只有我们两个人呢。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220011, /*unit_id*/122001, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/10.0, /*change_face*/5, /*description*/'生日快乐。\n认识的幽灵教给了我\n在海盗中流传的庆生歌曲，\n希望能让你开心。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1220012, /*unit_id*/122031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'为了庆祝你的生日，\n我来献上这支舞。\n我会用心起舞，\n祝愿你这一年都会幸福的哦。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221001, /*unit_id*/122101, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/'大自然果然非常美好～\n小鸟和花花草草们\n也聊得很热烈……\n让人心情平静♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221002, /*unit_id*/122101, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/4.4, /*change_face*/3, /*description*/'大自然无论何时都会接纳我！\n它现在已经迫不及待\n要迎接我了！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221003, /*unit_id*/122101, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/5.8, /*change_face*/2, /*description*/'城里人把山中隐居生活\n称为露营对吧？\n哇～露营～\n多么有品位的词汇啊！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221004, /*unit_id*/122101, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.4, /*change_face*/2, /*description*/'我以前在山中隐居的时候\n总穿这身衣服！\n这就像是面对\n大自然的时候穿的正装！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221005, /*unit_id*/122101, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/'从下次开始……把孤独空间\n也当成BB团的据点吧！\n那是我很重视的地方……\n不过随时欢迎你的到来！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221006, /*unit_id*/122131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'今晚我们畅谈一整夜吧？\n讨论一下BB团的今后！\n让大自然和星星都听一听\n我们摆脱孤独的宏伟计划吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221007, /*unit_id*/122131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我听说有一种叫做BBQ的事物，\n真是一个有亲切感的名字！\n一定是对孤独之人很友好的东西♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221008, /*unit_id*/122131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'呼，呼……身心都好暖和♪　明明只是一碗普通的汤，\n却有种特别的风味，\n肯定是因为此刻只有我们两个吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221009, /*unit_id*/122131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'两个人齐心协力\n搭帐篷还有做菜……\n在露营时分工合作\n能体会到不再孤独的感觉！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221010, /*unit_id*/122131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'夜晚的营地……\n真的好安静……\n就像世界上……\n只剩下我们两个一样！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221011, /*unit_id*/122101, /*use_type*/2, /*voice_id*/6, /*face_id*/7, /*change_time*/2.9, /*change_face*/4, /*description*/'生日快乐！\n啊哈哈，果然会紧张呢。\n我的问候没问题吧？\n声音听起来有没有不自然？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1221012, /*unit_id*/122131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'生快♪　对不起！\n我考虑了太久该怎么祝贺你，\n结果变得太简洁了！\n我重新说一遍，祝你生日快乐！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222001, /*unit_id*/122201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.3, /*change_face*/2, /*description*/'如何？　这套服装很棒吧？\n它可不仅仅是好看，\n在实用性方面也是出类拔萃。我在露营用品店一眼就看上了它，\n然后就买回来了♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222002, /*unit_id*/122201, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/3.1, /*change_face*/2, /*description*/'我又添置了\n新的露营用具……\n为了不浪费钱，今后还要\n和你再来很多次才行呢。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222003, /*unit_id*/122201, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/6.8, /*change_face*/1, /*description*/'嗯！　有种得到解放的感觉！\n光是这片美景，就足够\n当我喝麦酒的下酒菜了！\n来，我们开始扎营吧！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222004, /*unit_id*/122201, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/'嘿嘿嘿嘿♪\n就着露营的饭菜，\n痛痛快快喝了个够♪\n这一杯酒真是太棒了！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222005, /*unit_id*/122201, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/4.0, /*change_face*/1, /*description*/'露营虽然很开心，\n但也有不方便的地方呢……\n如果遇到麻烦，\n可以来找姐姐哦？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222006, /*unit_id*/122231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'吸……嗯，空气真清新……\n这样我就能永远喝下去了……\n大自然真是最好的下酒菜啊……\n要不要早上就来一杯呢……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222007, /*unit_id*/122231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我、我也会喝麦酒\n以外的饮料啦！\n而且，要是咖啡的话，\n就可以和你一起喝吧？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1222008, /*unit_id*/122231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'朝阳真的好美……\n这样的景色在兰德索尔\n可是很难见到的。\n好好欣赏吧♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
