INSERT INTO `unit_comments` VALUES (/*id*/1805005, /*unit_id*/180501, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/2.7, /*change_face*/2, /*description*/'“公主形态”……\n之所以会比平时更有力量，\n是因为内心深处\n强烈地感受到与主人之间的联系吧。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805006, /*unit_id*/180531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我的愿望只有一个……\n就是永远留在主人的身边。\n无论什么困难，我都会用这个力量克服。\n我一定会让这个愿望实现的！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805007, /*unit_id*/180531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'不管这趟旅程会迎来\n怎样的结局，我将永远\n为主人而战。我不会犹豫。\n这股力量就是一切的证明。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805008, /*unit_id*/180531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'变得更强，飞得更高……\n我要用这双由羁绊而生的翅膀，\n与主人一同飞向未来。\n无论何时何地……都要永远在一起……', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805009, /*unit_id*/180531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'我不想再看见\n主人受到伤害了。\n我将用这个力量保护您。\n让您永远保持笑容。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805010, /*unit_id*/180531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'感觉我和主人之间的距离\n越来越近了。\n主人您也是\n和我一样的感觉吗？', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805011, /*unit_id*/180501, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/'我一直衷心地期盼\n今日的到来。我会全心全意地\n为主人庆祝\n这个生日哦。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1805012, /*unit_id*/180531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'生日快乐。\n主人有什么特别想\n让我为您做的事吗？　如果有的话，\n请不要客气尽管告诉我。', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1806001, /*unit_id*/180601, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.0, /*change_face*/1, /*description*/'我也很习惯\n变成公主形态了呢！\n喂，要看着我哦？\n接下来我给你看一些厉害的！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
