INSERT INTO `unit_comments` VALUES (/*id*/1806004, /*unit_id*/180601, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/6.7, /*change_face*/1, /*description*/'像你这种没有底线的烂好人，\n如果我不保护你的话，\n还有谁能保护你呢。\n你应该对我心存感激♪', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
