INSERT INTO `unit_comments` VALUES (/*id*/1806006, /*unit_id*/180631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'公主，变身！\n我要用这力量，\n和你一起\n超越任何极限！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
