INSERT INTO `unit_comments` VALUES (/*id*/1806008, /*unit_id*/180631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'哼哼♪\n优秀的我所释放的完美大魔法，\n能够毫不费力地\n击飞任何敌人！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
