INSERT INTO `unit_comments` VALUES (/*id*/1806011, /*unit_id*/180601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.2, /*change_face*/3, /*description*/'生日快乐啦。\n难得今天是你的生日，\n大家一起开派对吧。\n但是食物里不可以有魔物哦！', /*all_comments_flag*/0, /*target_unit_id*/0, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
