INSERT INTO `unit_comments` VALUES (/*id*/1807001, /*unit_id*/180701, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/3.4, /*change_face*/1, /*description*/'下次我们一起出门吧！\n我在飞行的时候发现了\n一个非常棒的地方，\n想和栞栞一起去☆', /*all_comments_flag*/0, /*target_unit_id*/118301, /*face_id_2*/2, /*change_time_2*/0.0, /*change_face_2*/2, /*face_id_3*/2, /*change_time_3*/0.0, /*change_face_3*/2);
INSERT INTO `unit_comments` VALUES (/*id*/1807002, /*unit_id*/180701, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/3.8, /*change_face*/2, /*description*/'栞栞推荐的书我看过了☆\n一连串激动人心的冒险故事，\n真是太有趣了！', /*all_comments_flag*/0, /*target_unit_id*/118301, /*face_id_2*/2, /*change_time_2*/0.0, /*change_face_2*/2, /*face_id_3*/2, /*change_time_3*/0.0, /*change_face_3*/2);
