INSERT INTO `unit_comments` VALUES (/*id*/1807004, /*unit_id*/180701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.8, /*change_face*/6, /*description*/'姐姐，今天我们要一起去\n稍微远一点的地方冒险吗？\n我身体没问题。呵呵……\n姐姐真爱操心呢。', /*all_comments_flag*/0, /*target_unit_id*/118401, /*face_id_2*/2, /*change_time_2*/0.0, /*change_face_2*/2, /*face_id_3*/2, /*change_time_3*/0.0, /*change_face_3*/2);
