INSERT INTO `unit_comments` VALUES (/*id*/1807009, /*unit_id*/180731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'只要有我和栞栞的情谊以及你的温柔！\n无论面对怎样的对手，\n我都绝对不会输☆', /*all_comments_flag*/0, /*target_unit_id*/118301, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
