INSERT INTO `unit_comments` VALUES (/*id*/1807016, /*unit_id*/180731, /*use_type*/2, /*voice_id*/8, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'今天是个特别的日子呢。\n因为是你的生日呀。\n能像这样和你一起庆祝，\n我也觉得很开心。', /*all_comments_flag*/0, /*target_unit_id*/118401, /*face_id_2*/0, /*change_time_2*/0.0, /*change_face_2*/0, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
INSERT INTO `unit_comments` VALUES (/*id*/1808001, /*unit_id*/180801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/'你盯着我看了好久了，\n怎么了吗？　总感觉\n你的眼神很可疑……你果然\n是个变态可疑分子！', /*all_comments_flag*/0, /*target_unit_id*/120602, /*face_id_2*/1, /*change_time_2*/5.1, /*change_face_2*/3, /*face_id_3*/0, /*change_time_3*/0.0, /*change_face_3*/0);
