INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206700, /*unit_name*/'日和莉的暗影', /*prefab_id*/206700, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/2.295, /*cutin*/0, /*visual_change_flag*/0, /*comment*/'拥有日和莉外貌的特殊魔物\n\n·给予眼前的一名敌人高等物理伤害，\n　同时给予周围小范围内所有敌人\n　中等物理伤害。\n·给予眼前的一名i敌人中等物理\n　伤害。\n·给予眼前的一名敌人低等物理伤害，\n　并中幅度提升自己的物理攻击力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206701, /*unit_name*/'怜（新年）的暗影', /*prefab_id*/206701, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/400, /*atk_type*/1, /*normal_atk_cast_time*/1.965, /*cutin*/0, /*visual_change_flag*/0, /*comment*/'拥有怜（新年）外貌的特殊魔物\n\n·给予敌方全体中等物理伤害，\n　并眩晕对象。\n·【嘲讽】对敌方全体发出嘲讽。\n　嘲讽后的动作期间受到攻击时，\n　对范围内的所有敌人给予中等物理伤害，\n　并小幅度提升物理防御力。\n·对自己周围的所有同伴\n　展开魔法免疫屏障，\n　并中幅度提升自己的魔法防御力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206702, /*unit_name*/'优衣（新年）的暗影', /*prefab_id*/206702, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/600, /*atk_type*/2, /*normal_atk_cast_time*/2.27, /*cutin*/0, /*visual_change_flag*/0, /*comment*/'拥有优衣（新年）外貌的特殊魔物\n\n·对己方全体赋予物理/魔法免疫屏障，\n　及生命值持续回复状态，\n　但会大幅度降低自身魔法攻击力。\n·大幅度降低第二个敌人单体的\n　物理攻击力和魔法攻击力。\n·大幅度提升所有进行魔法攻击的己方角色的\n　魔法攻击力。');
