INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305400, /*unit_name*/'冰角龙', /*prefab_id*/305400, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/'【物理】强而有力的带冰头部能\n贯穿坚韧护盾的独角之兽。\n在布满冰丘的北方尽头，\n将冒险者推落到冰之地狱。\n\n·对敌方全体造成大量魔法伤害，\n　降低物理防御力。\n　并使己方全体的技能值\n　上升。\n\n·对前方小范围造成少量魔法伤害，\n　使其陷入结冰状态。并且\n　对全体造成少量魔法伤害，\n　将前方三名角色小幅度\n　击飞。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305500, /*unit_name*/'雾精', /*prefab_id*/305500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/2160, /*atk_type*/2, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/'【魔法】在无数传说中都有所记载的记忆之大精灵。\n没有实体，如雾般缥缈隐藏全身。\n\n·必杀技能给予敌方全体\n　高等魔法伤害，\n　并且在敌人处于诅咒状态时\n　提升伤害。\n·给予敌方全体中等魔法伤害。\n　并使其陷入诅咒状态。\n·随机召唤\n　分身（紫）（红）（蓝）之中的其中一个。\n　召唤时给予敌人全体反击。\n·【物理】分身（紫）会为自身展开吸收\n　物理/魔法的屏障。\n·【物理】分身（红）会给予敌方全体\n　中等魔法伤害。\n·【物理】分身（蓝）给予敌方全体\n　中等物理伤害。\n　并且使其陷入诅咒状态。\n　分身（蓝）的生命值降到30%以下时，\n　回复雾精的300技能值。\n·雾精自身的生命值\n　降到50%时，\n　会变得更为凶暴，攻击更为猛烈。');
