INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305600, /*unit_name*/'异界魔主', /*prefab_id*/305600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/'【魔法】渴望获得露娜魔力而出现的异界魔物。\n企图凭魔眼和自己率领的士兵征服露娜之塔。\n\n·对敌方全体造成大量魔法伤害\n　并且中幅降低敌方全体的魔法防御力。\n·随机六次对一名角色造成少量\n　魔法伤害。\n·对倒数第三名角色造成中等魔法伤害\n　以及长时间的拘束。\n　并对全体敌人造成少量魔法伤害。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305700, /*unit_name*/'巨型哥布林', /*prefab_id*/305700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/'豪爽地挥舞着战斧，熟练的哥布林战士。\n将年幼的战士夹在腋下，向其展示魂之技能。\n\n·必杀技能中幅提升自身的\n　物理攻击力，小幅提升物理防御力，并且\n　对前方一名角色造成物理伤害（特大）。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305800, /*unit_name*/'海蛞蝓', /*prefab_id*/305800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/'传说中的海神使者，神奇的软体魔物。\n全身基本由蕴含魔力的海水组成。\n\n·必杀技能给予敌方全体\n　高等魔法伤害。\n·给予第二名和第三名角色\n　中等魔法伤害。\n·在一定范围内，降低物理攻击力和\n　魔法防御力，展开持续给予魔法伤害的\n　领域。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305900, /*unit_name*/'极彩鸟', /*prefab_id*/305900, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.2, /*cutin*/0, /*visual_change_flag*/0, /*comment*/'释放压倒性的魅力，外表华美的极彩鸟。\n若被其的美貌诱惑，就会其攻击同伴的仆人。\n\n·必杀技能是给予处于诱惑状态的角色\n　之外的敌方全体高等物理伤害。\n·给予第二名角色低等魔法伤害\n　并赋予诱惑状态，并且其后大幅提升\n　物理攻击力和魔法攻击力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306000, /*unit_name*/'邪饼神咖米', /*prefab_id*/306000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/2160, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/'【物理】汇聚了邪念残渣化身而成的邪神。\n将显现出的全部烦恼都进行净化，迎接新年吧！\n\n·必杀技能给予敌方全体\n　高等物理伤害。\n·给予前方三名角色中等魔法伤害，\n　降低物理攻击力。\n·在前方中距离召唤倒计时式炸弹年糕。\n·炸弹年糕会在召唤后\n　8秒产生大爆炸，给予范围内的敌方全体\n　中等物理伤害。\n　但是，在爆炸前将炸弹年糕的生命值\n　降到50%，\n　则小幅回复敌方全体生命值，并消失。\n·邪饼神咖米自身的生命值\n　降到50%时，会变得更加凶暴，\n　攻击更为猛烈。');
