INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306100, /*unit_name*/'偷盗豺狼', /*prefab_id*/306100, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/'【物理】潜藏在黑暗之中，在村落中偷盗的\n魔物头领。\n毫不留情舍弃蒙骗回来的猎物，\n妨碍开拓的流氓。\n\n·给予前方三名角色中等\n　物理伤害，使其麻痹。\n\n·给予敌方全体中等物理伤害，\n　降低物理防御力和行动速度。\n　并且使前方小范围陷入\n　中毒状态。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306200, /*unit_name*/'狸猫首领', /*prefab_id*/306200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/'【物理】占领森林深处，和手下一同支配周边一带，\n老奸巨猾的狸猫。\n展现神秘的幻影，巧妙地操纵着侵蚀精神的\n幻影攻击。\n\n·给予敌方全体中等魔法伤害，\n　召唤两只淘气豆狸。\n\n·给予敌方全体低等魔法伤害，\n　让非无敌状态的召唤兽\n　消失。\n　并且当敌人的生命值处于40%以下时，\n　使其陷入麻痹状态。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306300, /*unit_name*/'长耳猴首领', /*prefab_id*/306300, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/'【物理】使用露娜魔力，统领长耳猴的仙猿。\n自在操纵宝武具之棍，击退所有敌人。\n\n·对敌方全体造成大量物理伤害\n　该攻击对处于烧伤状态的敌人，\n　伤害会变为1.8倍。\n·对前方中范围的敌人造成中等物理伤害\n·对前方小范围的敌方造成中等魔法伤害，\n　使其变成烧伤状态。');
